                                positions: pl2Positions,
                                width: 1,
                                material: new Cesium.Material({
                                    fabric: {
                                        type: 'PolylineDash',
                                        uniforms: {
                                            color: LINEPOINTCOLOR,
                                        }
                                    },
                                })
